    private void sbcargatextoActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_sbcargatextoActionPerformed
        // TODO add your handling code here:
        Papa p=new Papa();
        p.ponerNombre(nombrePapa.getText());
        String txt = p.decirAlgo(mensaje.getText());
        
        bitacora.append(txt+"\n");
        
    }//GEN-LAST:event_sbcargatextoActionPerformed

    private void sbcarganumeroActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_sbcarganumeroActionPerformed
        // TODO add your handling code here:
        Papa p=new Papa();
        p.ponerNombre(nombrePapa.getText());
        String txt = p.decirAlgo(Integer.parseInt(mensaje.getText()));
        
        bitacora.append(txt+"\n");
        
    }//GEN-LAST:event_sbcarganumeroActionPerformed

